INSERT INTO `roomextra` VALUES (277,'greenish','Hey!  That greenish panel conceals a secret door!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (277,'oak','The oak looks imported.  Except for one panel, which is colored greenish.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (277,'walls','The walls are stained, paneled oak.  Quite expensive!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (286,'sign','The sign reads:  \"Welcome to the armory.  Please check\nout all weapons at the main desk.\"\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (287,'junk','It\'s junk, see.  You don\'t want it.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (302,'sign large','Coming Soon:\n\n   Homer\'s new smash hit: The Odyssey.\n\n   Get your tickets now!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (317,'floor boards down','  Examining the floor closely the outline of a trapdoor becomes visible.\nAt one time it had a lock and key, but having to pass off the key between\nplayers became much too tedious.  Now every player is taught a simple trick\nto unlock the trapdoor.  Not knowing this trick will probably mean you\'ll\nhave to pick the lock to open it.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (322,'boxes wooden box','There are various props in there.  Nothing of value....\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (323,'staircase spiral','The spiral staircase leads down to the theater\'s basement.  It is made of\niron and has wooden treads.  It seems quite sturdy.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (327,'hole','This hole is round and you can actually see the stage from here!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (335,'trapdoor panel','  You examine the false doors closely to see how they are operated.\nAt one time they had locks and keys, but having to pass off the key between\nplayers became much too tedious.  Now every player is taught a simple trick\nto unlock the false doors.  Not knowing this trick will probably mean you\'ll\nhave to pick the lock to open it.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (338,'dart-board board','You see a large picture of the director.  Someone has taken an ink pen\nto the picture and has drawn a small bushy mustache on the director\'s\nupper lip.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (352,'port hole','You see a large road in the distance.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (353,'paintings','These colorful images seem to illustrate a great battle.\nIt appears our hero was a large human with a gray beard.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (359,'table dining oak','This table is large and ornate.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (361,'footprints','Whoever walked here has GIANT feet!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (363,'wine rack','This rack could hold 50 bottles....  Wow!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (394,'picture','A large prominent picture of the King\'s father sits on the wall.  He had a\nvery large nose.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (406,'sign large','We guarantee delivery for 50 Talens per letter.\n',NULL,NULL);
