INSERT INTO `roomextra` VALUES (1162,'vegetation plants plant-life','Thick hearty vegetation and plant life can be seen all about the area.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (1162,'hole fountain statue center-piece','  The hole in which the fountain has sunk is dark and almost impossible to\nsee into.  It apparently is quite deep as anything you drop over the edge\ntakes a bit before you hear it hit bottom.  The top of the statue that was\nonce the center-piece is now covered in thick vines and what little remains\nof it is slowly crumbling away and is now indistinguishable.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (1163,'path ','  The path is quite narrow and appears to be regularly cleared for at \nleast one or two people to walk single file.  The thin line of cleared\nearth below is hard packed as if traveled often.\n',NULL,NULL);
