INSERT INTO `roomextra` VALUES (2139,'sign stalagmite','Well if you are reading this you must be alive.  And to be alive you must\nhave either killed Kylish\'Ra or found a way to hide from her.  If you have\nkilled her, congratulations, you are truly a force to be reckoned with.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (2161,'note','Exit east will be an connector for extra mining tunnels from Icarus.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (2180,'north door','\"Lavatory,\" is carved in the door.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (2180,'south door','\"Supplies,\" is carved in the door.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (2180,'east door','\"Supervisor,\" is carved in the door.\n',NULL,NULL);
