INSERT INTO `roomextra` VALUES (3451,'hole opening','A large opening showing the inside of a hollow tree.  The inside appears\nscraped out by some animals, and has something that could be bedding\njust out of arms reach up the inside of the trunk.  It looks recently used.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (3482,'tree ancient','A huge ancient tree spanning at least 7 feet in diameter with thick gnarled\nbranches sprouting about 10 feet up.  The trunk when looked at, almost\nappears to have a face, but that could just be a trick of the light.\n',NULL,NULL);
