INSERT INTO `roomextra` VALUES (4635,'posters','  Several posters, all of questionable taste, are pasted to the wall.  One\nshows a comely young elf maiden, totally nude, frolicing in the woods with\na group of \"excited\" saytrs.  Another advertises a gladitorial bout at \nThraix\'s Arena with a picture of a bloody Skrell holding up the severed\nhead.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (4635,'notices','  These are the \"rules\" of the house.\n\n	1. All residents must pay for the night in advance!\n	2. NO CREDIT!\n	3. All residents must bathe at least once a week!\n	4. You puke, you clean it up!\n	5. No visitors!\n	6. No pets!\n',NULL,NULL);
