INSERT INTO `roomextra` VALUES (35701,'pile bones','The piles of bones look like they come from small mammals, perhaps mice or\nrats.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (6146,'door golden huge','The features of the door can not be made out from here, except that it is of\nlarge dimension.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (6146,'throne dark black','You are too far away to make out any details about the throne.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (6147,'dome','The dome has been engraved with intertwining serpents.\n',NULL,NULL);
