INSERT INTO `roomextra` VALUES (9525,'pulley rope loop','The rope loops through the pulley on the floor and disappears up into\nthe darkness.  Tugging gently indicates it that its solidly attached \nto the lift.  Wonder what could be accomplished with a good pull.\n',NULL,NULL);
