INSERT INTO `roomextra` VALUES (11106,'water','This sticky substance can hardly be called water.  It is more like thin\nblood.  Whatever comes in contact with it is soon stained red.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (11107,'water','This sticky substance can hardly be called water.  It is more like thin\nblood.  Whatever comes in contact with this stuff is soon stained red.\n',NULL,NULL);
