INSERT INTO `roomextra` VALUES (14316,'wheel water','  This wheel is probably at least 15 feet in diameter.  The flow of water\ndown Whitehawk creek spins the wheel, providing power to whatever is\ninside the building.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14316,'sign','  The sign reads:\n                  Waterford Shire Community Mill\n                       Established Year 118\n                    Avril Hairtoe, Proprietor\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14316,'home small','  A small stone house stands beside the road to the south.  From the size\nof the building, you would guess that the resident is fairly short, as\nthe building itself is only about six feet tall.  There are no doors or\nwindows on this side of the building.\n',NULL,NULL);
