INSERT INTO `roomextra` VALUES (14332,'mud brown thick','  The best way to describe this stuff is thick brown, smelly muck.  Your\nboots sink in several inches as you walk, and the mud clings to your feet\nlike a living thing, reluctant to let go.  This stuff seems too be\ncomposed of equal parts dirt, animal waste and rotting vegetable matter.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14333,'stalls animals','  The western part of the barn is divided into several stalls, each\ndesigned to hold one animal.  Each stall is filled with hay, and has\ntroughs to hold water and food for the animals.\n',NULL,NULL);
