INSERT INTO `roomextra` VALUES (14344,'garden','  To the west lies the vegetable garden, used to provide fresh produce\nfor the farmer\'s family.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14344,'barn','  This structure is about 20 feet wide and made of wood.  It is a single\nstory, with a slate roof.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14344,'pasture','  This rolling grassy area lies up the hillside to the north of you.  It\nis completely enclosed by the fence which separates it from you.\n',NULL,NULL);
