INSERT INTO `roomextra` VALUES (14353,'wall wooden stout','  This wall is about 8 feet high, and made of thick wooden planks nailed\ntogether.  It appears quite sturdy.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14353,'gate north wooden','  Directly to your north, a wooden gate pierces the wall.  There is no\nsign of a lock.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14353,'burrows','  Seen from the outside, the hobbit burrow appears to be nothing more\nthan a small hillock into which a round door and a couple of windows have\nbeen set.  A small chimney protrudes from the top of the hillock.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14353,'door round','  The door is round in shape and about 5 feet in diameter.  It is made of\nwooden boards, and while there is a latch, there is no sign of a lock.\n',NULL,NULL);
