INSERT INTO `roomextra` VALUES (15988,'plaque','<o>The plaque is small and made of brass.  You can make out some words\nalong it which read:\n******************\nNatural Studies Section\n******************\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14354,'sign picture','  The sign displays a colorful picture of a large black bear, dancing\natop a round ball.  Above the picture, the words \"Dancing Bear Inn and\nTavern\" are written in flowing script.  Below the picture, in block\nletters, are the words \"Establish Year 118, Three-Toes Broadfoot,\nProprietor.\n',NULL,NULL);
