INSERT INTO `roomextra` VALUES (14357,'office','  To the north lies the office of the Shiriff, the chief law enforcement\nofficer of the Shire.  To the south lies the office of the Burgermeister,\nthe leader of the village.  Both building are small affairs made of stone.\n',NULL,NULL);
