INSERT INTO `roomextra` VALUES (14389,'building stone','  The building is constructed of large blocks of stone, roughly cut and\nmortared together.  The building is built into the side of a low hill,\nand obviously continues underground.  There are no openings on this side\nof the building.\n',NULL,NULL);
