INSERT INTO `roomextra` VALUES (14450,'patch pipeweed lowlands','  A small patch of broadleaf pipeweed plants grows in the low swampy area\nalong the south bank of the creek.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14451,'fence','  The fence is made of split wooden rails.  It is sturdily constructed,\nand you can see no way through.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14451,'pasture','  This broad area of thick green grass occupies the lowlands just beyond\nthe fence.  You can see some animals in the distance, but you are not\nsure what kind.\n',NULL,NULL);
