INSERT INTO `roomextra` VALUES (14455,'farm','  A small farm stands on the eastern bank of the pond.  There is a\npasture to the northeast, and the back of a small barn lies to the east.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14455,'building wooden','  This two story building stands right on the water\'s edge.  There is a\nlarge water wheel on the western side.\n',NULL,NULL);
