INSERT INTO `roomextra` VALUES (14461,'building brick imposing','  Made of brick, this building is the largest and most impressive in the\nshire.  A single window overlooks the creek, but you are too low to be\nable to see inside.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14461,'grain field','  This small field has been planted with some type of grain, probably\nwheat.  The grain stands too thickly for you to make force a passage\nthrough it.\n',NULL,NULL);
