INSERT INTO `roomextra` VALUES (14463,'bridge','  Made of a silvery stone, this bridge crosses the creek, carrying a road\nfrom the western bank into the shire on the eastern bank.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14464,'building large','  A normal human size building of wood sits beside the creek.  There are\nno windows on this side.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14464,'grassland rolling open','  An area of gently rolling hills covered in tall grass occupies the\nwestern bank of the creek as far as the eye can see.  An occasional clump\nof trees breaks the monotony of the view.  You can discern no trails\nacross this grassland.\n',NULL,NULL);
