INSERT INTO `roomextra` VALUES (14474,'dock small','  A small loading dock, covered with boxes and crates of all types lies\njust to the west.  It is attached to the back of a wooden building.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14474,'shire','  Beyond a stout wooden wall, which runs right to the rivers edge, you\ncan see the roofs of a number of small buildings.  The steeple of a\nchurch can be seen rising from the center of the village, or shire as it\nis known in this part of The World.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (23153,'pile obsidian','Stacked loosely against the wall sits pile of obsidian.  From the looks of\nit, the pile may have been moved before.\n',NULL,NULL);
