INSERT INTO `roomextra` VALUES (14497,'sea Agorian','  Stretching to the horizon in the west, you see the vast expanse of the\nAgorian Sea.  The cobalt blue color of the water tells you it is quite\ndeep, and the whitecaps whipped up by the steady sea breeze make you\nrealize that you would need a large ship with a good crew to safely\ntravel this watery frontier.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (14497,'city white','  This must be the fabled city of Brightmoon.  Far in the distance, you\ncan see no way to get there over the broken ground of the seashore.\n',NULL,NULL);
