INSERT INTO `roomextra` VALUES (15373,'crack','small crack on the north wall.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (15400,'ground soil dirt earth','The earth here is a rich loamy dark brown.  This field clearly has not been\nfarmed all that much recently, and looks as if it will produce beautiful\ncrops for years to come.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (15400,'corn','This field is so well tended, and the soil so excellent, that a good harvest\nis insured.  Of course you think back to the last thunderstorm, and note just\nhow frail corn really is.  This crop actually is quite a gamble, and you\ncan\'t help but have a bit of respect for the brave individuals who gamble\ntheir very lives on such an unsure future.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (15401,'corn','This field is so well tended, and the soil so excellent, that a good harvest\nis insured.  Of course you think back to the last thunderstorm, and note just\nhow frail corn really is.  This crop actually is quite a gamble, and you\ncan\'t help but have a bit of respect for the brave individuals who gamble\ntheir very lives on such an unsure future.\n',NULL,NULL);
