INSERT INTO `roomextra` VALUES (16221,'sign wall','Welcome to the Psychiatric ward.  BEWARE all those who enter here.\nThe hospital administration takes no responsibility in what may\nhappen to you.  <y>ENTER AT OWN RISK!<1>\n',NULL,NULL);
