INSERT INTO `roomextra` VALUES (20519,'camp bones meals scraps','  It appears as if the camp has been used quite recently.  The bones still\nhave tidbits of flesh attached to them which local fish and water creatures\nhave not scavenged.  Nothing else of any real significance jumps to your\nattention.\n',NULL,NULL);
