INSERT INTO `roomextra` VALUES (20664,'flap slit','It seems the tent has but one exit which is a reinforced slit cut into its\nside with a simple flap of thick hide affixed over the opening which can be\npulled over to conceal or pushed aside to reveal the opening.\n',NULL,NULL);
