INSERT INTO `roomextra` VALUES (25408,'map','The map dates a long way back to a time when Grimhaven was much\nfarm land.  Faded and torn, the only clear things on the map located\nin the right hand corner are the words L\'yrel 231.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (25408,'green','This book is faded and much of it is burned, scanning through it\nwith your fingers your eyes fall upon the words :DarkO#@!&*-:.\nThe rest of the lettering is illegible.\n',NULL,NULL);
