INSERT INTO `roomextra` VALUES (27800,'seagull','  Rats with wings, these aerial scavengers have been know to steal food right\nout of peoples hands! They seem to glide effortlessly through the air on the\ngentle breezes wafting of the bay.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (27800,'_tele_',' Oh no! A seagull has decided that you\'re a target.  Nimbly you leap out of\nthe way just in time to avoid its messy bomb!\n',NULL,NULL);
