INSERT INTO `roomextra` VALUES (27803,'glint','AHA! Leaning perilously far over the edge you see a thin wire that leads\ndownwards and is attached to a stout, if rusted, ladder.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (27803,'pillar',' This pillar is as plain and ordinary looking as the others aside from being\nclean.  As you peer closer at it you see the sunlight glint off a small\nmetallic piece of something sticking out over the edge.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (27803,'droppings',' The seagulls either avoid dive bombing the eastern pillar or someone has been\nkeeping it cleaned and free of debris.\n',NULL,NULL);
