INSERT INTO `roomextra` VALUES (27899,'blue blueprints prints','These blueprints are so scattered and strewn about it would take you weeks\nto figure out what they truly represent.  As far as you can make out they\nseem to be plans for a new, large warship.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (27979,'_tele_','With a loud WOOOOSH you are expelled from the Gargantuan\'s arse.  You\nsuddenly find youself flying through the air, the land screams past\nbelow you.  Miraculously you land safely without a scratch!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (22771,'cove beach','<y>The cove below is surrounded by a beach except where it opens to the Agorian Sea to the north.<1>\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (22771,'peninsula','<o>Across the cove to the west, this small peninsula protrudes north, almost\nreaching\nthe mainland.<1>\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (22771,'wooden fence','<o>The fence runs north-south along the cliff, protecting you from stepping\ntoo far out on the crumbling ledge.<1>\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (27992,'_tele_','A low rumble shakes the entire room.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (28016,'flashes guano metallic','The light is reflecting off a scrap of metal, it looks like it used to be a\nhelm.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (28017,'metallic flashes guano','The light is reflecting off a scrap of metal, it looks like it used to be a\nbreastplate.\n',NULL,NULL);
