INSERT INTO `roomextra` VALUES (30978,'spiral staircase','The stairs are made of strong, old wood and wrought iron. They rise up \nto a hole in the keystone of the arched ceiling.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30980,'lintel','Engraved with figurative images of Gnomish knowledge, the center of the lintel\nreads simply, \"Histories of Gnath\".\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30981,'carved columns','Stretching the length of the columns are images of withered, sallow-eyed gnomes,\ntheir thin fingers stroking stone birds.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30982,'dust','Numerous little smudges disturb the layer of dust, marking the passage of \ntiny hands and feet.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30983,'grime dust','Smudges and patches of worn grime mark the passage of tiny hands and feet.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30984,'grime','Smudges and patches of worn grime mark the passage of tiny hands and feet.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30984,'ledge shaft','A narrow, precarious ledge wraps around the barrel of the well.  It would take\na skilled climber to negotiate their way around.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30985,'tiles walls','The tiles are of varying size and thickness. Their coloration ranges from pale\nblues and greens to reddish browns and mauve. Wide gaps of grouting between \nthem might suffice as foot and toe-holds for adept climbers.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30985,'ledge shaft','A narrow, precarious ledge wraps around the barrel of the well.  It would take\na skilled climber to negotiate their way around.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30987,'ledge','Irregularities in the stone wall afford a climbable path up the the \nledge overhead.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30987,'_tele_','A bucket flies in from above, hitting the water with an insolent SPLASH!\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30989,'columns','The dirty stone floor is marked by scattered footprints and hand smudges. Some\nof them climb surprisingly high up the walls and columnar supports.\n',NULL,NULL);
