INSERT INTO `roomextra` VALUES (30997,'path tiles','The matte black tiles are of a soft stone, providing some modicum of comfort for\nseated mourners. They completely encircle the room, crossing in front of the \narched portal.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30997,'engraved stonework','Reliefs of deadpan gnomes stand one upon the other, and lie head to toe on the\nhorizontal slab over the portal.\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (30997,'canopy gauze ceiling','Upon careful scrutiny, a shadowy tableau of ghostly gnomes is visible behind the\nthin gauze, their arms raised in prayer.\n',NULL,NULL);
