INSERT INTO `roomextra` VALUES (16257,'strap leather ','This strap appears to be part of a large harness.  The strap itself is nearly \nsix inches in diameter.  Judging by the frayed end, something rather \nlarge bit through the strap.  This was probably not a harness used for a \nbroken down horse.  \n',NULL,NULL);
INSERT INTO `roomextra` VALUES (16257,'red scale scales ','The red scales are about the size of a small shield, and seem to\nhave been left behind by a large red reptile.  \n',NULL,NULL);
