INSERT INTO `roomextra` VALUES (4377,'ferns','<g>Ferns have flourished in this moist environment, and there is a\nlarger clump of ferns in one spot on the muddy ground which appear to\nbe extra thick and lush.<z>\n',NULL,NULL);
INSERT INTO `roomextra` VALUES (13428,'storm','The storm appears to be very fierce.  Vision into it is almost\nzero and you have to avert your eyes often to keep from being\nblinded by the tremendous blasts of lightning which split the air\nwith an ear splitting crack every few seconds.  Strong gusts of wind\nblow against your face from the storm.\n',NULL,NULL);
