INSERT INTO `roomextra` VALUES (45394,'wall stone','The stone on the northern section of the wall is unlike the other rock in the area; it\'s much harder.  The regular stone appears to be interlaced with veins of a crystal clear, opaque like substance that is obviously quite strong.  The workers gave up on this area long ago.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45406,'crutches makeshift','<o>A broken pair of makeshift crutches leans uselessly against the wall.<z>',NULL,NULL);
INSERT INTO `roomextra` VALUES (45407,'outline','To the north is an outline etched into the wall.  Upon further inspection it resembles the shape of an altar.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45407,'stone','To the north is an outline etched into the wall.  Upon further inspection it resembles the shape of an altar.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45408,'cracks','The cracks on the altar are starting to spread and have formed a neat outline around the altar itself.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45408,'altar','The altar is very small and seems out of place in such a large room.  The maintenance of it has been poor and you see small cracks starting to form. It is pushed against the south wall.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45408,'outline','Tracing the outline around the altar, it appears you might be able to wedge it open.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45409,'panel sheer stretched skin','<o>A sheer panel of stretched skin is an exit south.<z>',NULL,NULL);
INSERT INTO `roomextra` VALUES (45410,'colonies huge mold','<G>Huge colonies of mold are in full bloom and grow on every surface in the room.  Different from moss, this mold is slimy, and the <p>sp<k>or<p>es<z> <G>seem to shimmer and move when anything gets near them.<z>',NULL,NULL);
INSERT INTO `roomextra` VALUES (45420,'crates destroyed ceiling','The wooden crates sticking out of the ceiling have turned into wooden pieces of rubble due to the severe water damage they\'ve suffered.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45420,'wooden rubble','The wooden rubble looks like it could be opened enough to allow you to squeeze through.',NULL,NULL);
INSERT INTO `roomextra` VALUES (45426,'_tele_','<W>A downdraft of hot air slams into you sending you flying off the bridge!<z>\n\n<R>You spin out of control and tumble downwards.\n\nYOU\'RE FALLING!!!\n\nYOU\'RE FALLING!!!\n\n<W>The turbulent wind batters you and forces you into the <B>sinkhole<z><W>!<z>\n\n<Y>NO ONE CAN HEAR YOUR SCREAMS!!<z>\n\n<z>',NULL,NULL);
