INSERT INTO `mobresponses` VALUES (100,'# City Watchman\n# Maror\n# Copyright 2004, SneezyMUD Development Team.  All rights reserved.\n\nroomenter {\"\";\n  nod;\n}\n\npackage {\"1\";\n  say Hello, citizen.;\n}\n\nsay {\"hello\";\n  link package 1;\n}\n\nsay {\"hi\";\n  link package 1;\n}\n\nnod {\"me\";\n  link package 1;\n}\n'),(10004,'# Marza\n# Rainbow Bridge Portal Quest\n# Created by Satan\n# Copyright 1997, SneezyMUD Development Team.  All rights reserved.\n\nsay { \"hello\";\n	look %n;\n 	say Hello %n, what can I do for you?;\n	}\n\nsay { \"nothing\";\n	shrug;\n	say Then beat it, fan boy.;\n	}\n\nsay { \"portal\";\n	think;\n	tell %n I might be able to arrange something for you...;\n	tell %n I haven\'t had any cheese in ages...;\n	tell %n But then again, cheese gives me gas...;\n	tell %n Hrm...I know - bring me some rump.;\n	tell %n I\'ll see what I can do, OK?;\n	}\n\nsay { \"OK\";\n	ask %n So where\'s the rump?;\n	tap;\n	}\n\nsay { \"rainbow\";\n	link say portal;\n	}\n\nsay { \"bridge\";\n	link say portal;\n	}\n\ngive { \"7744\";\n	load 10063;\n	beam %n;\n 	tell %n Thank you much!;\n	toroom $n rummages about in his pockets and produces a strange light.;\n	toroom He quickly puts his hand into the river and splashes some water up high!; \n	drop bridge;\n	say Now hurry, I have no idea how long it will last!;\n	}\n\ngive { \"23655\";\n	load 10063;\n	beam %n;\n 	tell %n Thank you much!;\n	toroom $n rummages about in his pockets and produces a strange light.;\n	toroom He quickly puts his hand into the river and splashes some water up high!; \n	drop bridge;\n	say Now hurry, I have no idea how long it will last!;\n	}\n\nsay { \"fear\";\n	say blah blah fear this blah fear that...;\n	wedgie %n;\n	say You chumps are all the same.;\n	}\n \nsay { \"laf\";\n	taunt %n;\n	say %n, you are a freak. It\'s spelled L-A-U-G-H.;\n	toroom $n snickers in disgust, shaking $s head.;\n	}\n \nsay { \"bog\";\n	say Bog...isn\'t that like a marsh or swamp..?;\n	peer %n;\n	say This lamer must be a Geno-wannabe.;\n	}\n\n say { \"Satan\";\n	roar;\n	say Damn, Satan is THE MAN.  Genuflect before the master!.;\n 	}\n\n say { \"wtf\";\n 	scratch head;\n	say wtf...? Does that mean women to freak?;\n	say or maybe...wedgies to finger?;\n	ponder;\n 	snap;\n	say I got it! Whale tongue filets!;\n	}\n'),(1004,'# Robber\n# added by Maror\n# Copyright 2004, SneezyMud Admin.  All rights reserved.\n\nroomenter {\"\";\n  random 10;\n    randoption 1;\n    randoption 2;\n    randoption 3;\n    randoption 4;\n    randoption 5;\n    randoption 6;\n    randoption 7;\n    randoption 8;\n    randoption 9;\n    randoption 10;\n      say Hi there.;\n  donerand;\n}\n\npackage {\"1\";\n  say Well, now that we are done with the formalities.;\n  hit %n;\n}\n\nsay {\"hello\";\n  link package 1;\n}\n\nsay {\"hi\";\n  link package 1;\n}\n\n'),(10102,'# Satyr - 10102\n# Barter Quest for Dryad\'s Footprints - 249\n# Written by Mithros\n# Copyright 2000 by SneezyMUD Administration\n\nsay {\"hello\";\n wink %n;\n}\n\nsay {\"footprint\";\n say Looking for the footprint of a dryad?;\n say They are difficult to find I must say.;\n grin;\n say Unless you know <W>where to look<1>.;\n}\n\nsay {\"where to look\";\n smile;\n say I don\'t give up my secrets that easily.;\n say But I\'ll get one for you for a <W>price<1>.;\n}\n\nsay {\"price\";\n grin %n;\n say Well let\'s see.;\n say It will cost you either, #OBJCOST( 249, 50)# talens, or...;\n say ...a good bottle of Bayle\'s Wine.;\n say The red mind you, none of that sissy white stuff.;\n}\n\ngive {\"-#OBJCOST( 249, 50)#\";\n smile %n;\n say This seems about right.;\n toroom $n seems to look around a bit.;\n toroom $n apparently spots what he is looking for and grabs it.;\n say Here we go, a dryad\'s footprint.;\n load 249;\n give footprint-component %n;\n say Just let me know if you need more.;\n}\n\nbuy {\"#OBJCOST(249, 50)# 1 footprint-dryads-component\";\n link give #OBJCOST(249, 50)#;\n}\n\ngive {\"2853\";\n grin;\n toroom $n seems to judge the weight of the bottle.;\n frown;\n say Seems a bit light...;\n say But no matter, even empty it leaves enough of its...;\n say ...essence to add flavor to my own brew.;\n load 249;\n toroom $n seems to look around a bit.;\n toroom $n apparently spots what he is looking for and grabs it.;\n say Here we go, a dryad\'s footprint.;\n give footprint-component %n; \n say Now be off and let me enjoy my drink!;\n}\n\nsay {\"brew\";\n say I think not...;\n say My brew is my own concern and you\'ll not be getting its recipe.;\n say Not fit for one of your kind anyways, yer stomach\'s too weak.;\n}\n\nsay {\"recipe\";\n link say brew;\n}\n\n'),(10103,'# Satyr - 10102\n# Barter Quest for Dryad\'s Footprints - 249\n# Written by Mithros\n# Copyright 2000 by SneezyMUD Administration\n\nsay {\"hello\";\n wink %n;\n}\n\nsay {\"footprint\";\n say Looking for the footprint of a dryad?;\n say They are difficult to find I must say.;\n grin;\n say Unless you know <W>where to look<1>.;\n}\n\nsay {\"where to look\";\n smile;\n say I don\'t give up my secrets that easily.;\n say But I\'ll get one for you for a <W>price<1>.;\n}\n\nsay {\"price\";\n grin %n;\n say Well let\'s see.;\n say It will cost you either, #OBJCOST( 249, 50)# talens, or...;\n say ...a good bottle of Bayle\'s Wine.;\n say The red mind you, none of that sissy white stuff.;\n}\n\ngive {\"-#OBJCOST( 249, 50)#\";\n smile %n;\n say This seems about right.;\n toroom $n seems to look around a bit.;\n toroom $n apparently spots what he is looking for and grabs it.;\n say Here we go, a dryad\'s footprint.;\n load 249;\n give footprint-component %n;\n say Just let me know if you need more.;\n}\n\nbuy {\"#OBJCOST( 249, 50)# 1 footprint-dryads-component\";\n link give #OBJCOST( 249, 50)#;\n}\n\ngive {\"2853\";\n grin;\n toroom $n seems to judge the weight of the bottle.;\n frown;\n say Seems a bit light...;\n say But no matter, even empty it leaves enough of its...;\n say ...essence to add flavor to my own brew.;\n load 249;\n toroom $n seems to look around a bit.;\n toroom $n apparently spots what he is looking for and grabs it.;\n say Here we go, a dryad\'s footprint.;\n give footprint-component %n; \n say Now be off and let me enjoy my drink!;\n}\n\nsay {\"brew\";\n say I think not...;\n say My brew is my own concern and you\'ll not be getting its recipe.;\n say Not fit for one of your kind anyways, yer stomach\'s too weak.;\n}\n\nsay {\"recipe\";\n link say brew;\n}\n\n'),(10115,'# Jendar - 10115\n# Barter Quest - jaguar bowstring [27119] for jaguar guts [27118]\n# Barter Quest - arrows [3410] for sticks [27123]\n# Written by Mithros\n# Copyright 1999 SneezyMUD Administration\n\nsay {\"hello\";\n smile %n;\n say Hello, may I assist you?;\n say If not please excuse me, I must get back to my task.;\n}\n\nsay {task;\n say Yes, I\'ve just returned from a hunting expedition.;\n say I need to replenish my stock of bowstrings and arrows.;\n say I was just beginning.;\n}\n\nsay {\"bowstring\";\n say Bowstrings?  Why yes I make quite a good bowstring.;\n say But to make the best you must use special gut material.;\n say I use nothing but jaguar guts, they seem to work best.;\n say If you have some we can trade.;\n say My skill for half the string?;\n}\n\nsay {\"arrow\";\n say Arrows?  Well my supply is running short.;\n say And not my best work.;\n say I am having trouble finding some good straight sticks.;\n say But if you can find me some...;\n say I\'ll trade you some of my other arrows.;\n}\n\ngive {\"27118\";\n toroom $n throughly examines the gut material.;\n say Yes this should work well.;\n say Here to save you time I\'ll give you what I have.;\n say So you don\'t have to wait while I work.;\n load 27119;\n give bowstring-jag %n;\n say And my thanks.;\n bow;\n}\n\ngive {\"27123;\n toroom $n throughly examines the stick for any flaws.;\n say This will work decently.;\n say Let me get you one of my other arrows in trade.;\n toroom $n digs through his things.;\n load 3410;\n give arrow %n;\n say Thank you.;\n}\n'),(10118,'# Rangui\'s Caretaker - 10118\n# Barter Quest for Green Falcon Feathers - 206\n# Written by Mithros\n# Copyright 1999 by SneezyMUD Administration\n\nsay {\"hello\";\n smile %n;\n say Can I help you?;\n say I must take my walk with Rangui soon.;\n}\n\nsay {\"Rangui\";\n say Yes Rangui is seer of the Elven Village.;\n say We take walks daily to help keep his strength up.;\n say Sometimes we walk during the day...;\n say Other times late at night when he can\'t sleep.;\n say We see some of the prettiest wildlife on our walks.;\n}\n\nsay {\"walk\";\n link say Rangui;\n}\n\nsay {\"wildlife\";\n say Oh yes, the wildlife about the Village is amazing.;\n say I find the birds especially fascinating.;\n say I try to collect the feathers I find for decorations.;\n}\n\nsay {\"feather\";\n say Yes I love to collect the feathers and decorate my small abode.;\n say I have quite an abundance of them now.;\n say Were you looking for any particular ones?;\n say Perhaps if you tell me the color and type of bird?;\n}\n\nsay {\"bird\";\n link say feather;\n}\n\nsay {\"falcon\";\n say Yes the falcon is a beautiful bird.;\n say I see many about the forest, although...;\n say they seem to remain well hidden.;\n say But my elven eyes and training help me..;\n say spot them easy enough.;\n say I especially like the Peregrine Falcons.;\n}\n\nsay {\"green falcon\";\n say I don\'t recall any green falcons.;\n say Although the Peregrine Falcons sometimes have...;\n say a few miscolored feathers which appear green.;\n say I\'ve found many of those feathers on our walks.;\n say Seems the bird tends to pluck them out on its own.;\n say I have several I found yesterday if you need some.;\n say They are excellent for mages to use in their spells.;\n say And I suppose I could use the talens.;\n say Rangui\'s medicines are quite expensive.;\n say How about #OBJCOST( 206, 20)# talens per feather?;\n}\n\nsay {\"peregrine falcon\";\n say A magnificent bird, all grey and brown.;\n say Although they sometimes have a few miscolored...;\n say feathers which appear green.;\n say I\'ve found many of those feathers on our walks.;\n say Seems the bird tends to pluck them out on its own.;\n say I have several I found yesterday if you need some.;\n say They are excellent for mages to use in their spells.;\n say And I suppose I could use the talens.;\n say Rangui\'s medicines are quite expensive.;\n say How about #OBJCOST( 206, 20)# talens per feather?;\n}\n\ngive {\"-#OBJCOST( 206, 20)#\";\n smile %n;\n say This seems about right.;\n toroom $n sorts through his things looking for the right feather.;\n say Here we go, a green falcon\'s feather.;\n load 206;\n give feather-component-green %n;\n say Just let me know if you need more.;\n}\n\nbuy {\"#OBJCOST(206, 20)# 1 feather-green-falcon-component\";\n link give #OBJCOST(206, 20)#; \n}\n\n'),(10119,'# Gersard\n# Spell Quest - Tornado\n# Created by _Unknown_\n# Copyright 1998, SneezyMUD Development Team.  All rights reserved.\n\nsay { \"hello\";\n	checktoggle 103;\n	say Yes?  What can I do for you %n?;\n	}\n\nsay { \"spell\";\n	checktoggle 103;\n	say And which spell would that be?;\n	}\n\nsay { \"tornado\";\n	checktoggle 103;\n	checkuntoggle 104;\n	toroom $n\'s face darkens slightly.;\n	say For me to grant you such a spell...;\n	say you must first show your alliance to us.;\n	say Perhaps if you were willing to <p>help<z> us...;\n	say maybe I would teach you the spell in return.;\n	}\n\nsay { \"tornado\";\n	checktoggle 104;\n	toroom $n\'s face grows angry.;\n	say Do not waste my time you fool.;\n	say You have already acquired the secrets to control the winds.;\n	say Do not bother me anymore with such silly requests.;\n	growl %n;\n	}\n\nsay { \"help\";\n	checktoggle 103;\n	checkuntoggle 104;\n	tovict $n looks at you thoughtfully.;\n	tonotvict $n looks at $N thoughtfully.;\n	say If you <p>prove<z> you are allied to us...;\n	say I will bestow the powers of the wind on you.;\n	}\n\nsay { \"powers of the wind\";\n	checktoggle 103;\n	checkuntoggle 104;\n	say The powers that will grant you the ability...;\n	say to summon a tornado upon your foes of course.;\n	}\n\nsay { \"prove\";\n	checktoggle 103;\n	checkuntoggle 104;\n	say To prove yourself you must help our village...;\n	say gain an advantage over our evil cousins.;\n	say Bring me their symbol of Chaos so that i may destroy it...;\n	say the Medallion of Eternal Chaos;\n	toroom $n turns around and goes back to $s work.;\n	}\n\nsay { \"evil cousins\";\n	checktoggle 103;\n	checkuntoggle 104;\n	say Our evil cousins, the drow.;\n	say You may find them by traveling southeast of Grimhaven.;\n	}\n\ngive { \"9920\";\n	checktoggle 103;\n	checkuntoggle 104;\n	tell %n Noble Hero and Elven Friend...;\n	tell %n you have proven your worthiness and your purity.;\n	toroom $n looks at the medallion speaking a word of power and crushes it with $s hands;\n	smile %n;\n	tell %n You have earned my respects.;\n	tell %n Please accept this spell as a gift from me.;\n	toroom $n holds his hands up high speaking words of power, while $s eyes flashes darkly.;\n	tovict $n holds your head in $s hands, closes $s eyes and slowly gives you a full soft kiss in the mouth that lasts a moment.;\n	tovict You can feel the signals and mantras of the spell flooding through your mind.;\n	tonotvict $n holds %n\'s head in $s hands and closes $s eyes and gives $M a full kiss in the mouth for a moment.;\n	toggle 104;\n	untoggle 103;\n	tonotvict %n appears to be mesmerized for a second then looks back and just doesn\'t seem to be the same person you knew.;	   \n	toroom $n smiles at you, then goes back to his work.;\n	}\n\n# Immortal Quest\n# Created by Satan\n# Copyright 1997, SneezyMUD Development Team.  All rights reserved.\n\nsay { \"morality\";\n	checktoggle 29;\n	beckon %n;\n	say So %n, I see that you wish to join the ranks of the Immortals.;\n	say Remember, the path is fraught with peril and adversity.;\n	say Persevere and you shall enjoy sweet rewards.; \n	say Here, I believe that you will need this...;\n	personalize 7796;\n	bow %n;\n	}\n'),(10126,'# Half Elven Ranger - Near Entrance to Elven Tree Village\n# Ranger Quest Lvl 14\n# Created by Mithros\n# Copyright 1998, SneezyMUD Development Team.  All rights reserved.\n\n\nsay {\"sent by a brother ranger\";\n      checktoggle 179;\n      whisper %n SHHH, you\'ll scare away the hawk I\'m observing.;\n      whisper %n What might you need?;\n}\n\nsay {\"bone woman\";\n      checktoggle 179;\n      whisper %n Oh the old pygmy woman?;\n      whisper %n You can find here within the walls of the Pygmy Village.;\n      whisper %n It is south of here past the glade of the Nymphs.;\n      whisper %n Look for an elderly Pygmy woman who has lost her sight.;\n      toroom $n slips back into the underbrush.;\n      toroom A perfect bird call suddenly breaks the silence around you.;\n}\n'),(1024,'# Bulge the Ogre\n# Toll collector mob at Great Eastern Crossroads\n# Created by Moath - June 06 1998\n# Modified by Dolgan - June 07 1998\n# Copyright 1998, SneezyMUD development team.  All rights reserved.\n\nroomenter { \"\";\n	tonotvict $n says something to %n.;\n	tell %n Hold it scum!;\n	tell %n No one gets by without paying me a toll.;\n	tell %n Ever since the drow have been raiding this road...;\n	tell %n Logrus has been forced to guard it.;\n	tell %n In return for that service you must pay a toll of 500 talens.; \n	checktoggle 129;\n	untoggle 129;\n	}\n\nsay { \"scum\";\n	tonotvict $n sneers at %n.;\n	tovict $n sneers at you contemptuously.;\n	tonotvict $n says something to %n.;\n	tell %n You heard me scum!;\n	tell %n Want to make something of it??;\n	}\n\nsay { \"toll\";\n	toroom $n smiles greedily.;\n	tonotvict $n says something to %n.;\n	tell %n If you want to get past me...;\n	tell %n Youâ€™re going to have to fork over 500 talens.;\n	tell %n Or you can wait till nightfall...;\n	tell %n and take a chance with the drow.;\n	}\n\nsay { \"pay\";\n	link say toll;\n	}\n\nsay { \"talens\";\n	link say toll;\n	}\n\nsay { \"past\";\n	toroom $n sighs.;\n	say Yeah I can\'t stop you from going back the way you came...;\n	say But don\'t even try and get past me without paying!;\n	}\n\nsay { \"drow\";\n	toroom $n looks furtively about.;\n	say There have been drow raiders attacking the roads lately.;\n	say The city of Logrus has managed to keep them at bay during the day...;\n	say But it has proven to be too costly to guard the roads at night.;\n	say Only a fool would travel these roads at night!;\n	}\n\nsay { \"Logrus\";\n	link say drow;\n	}\n\nsay { \"guard\";\n	link say drow;\n	}\n\nsay { \"road\";\n	tonotvict $n whispers to %n.;\n	tovict $n leans over and whispers to you.;\n	tell %n Well I\'m actually only supposed to charge...;\n	tell %n for using the Redstone Road...;\n	tell %n but I happen to like my job...;\n	tell %n so I just charge for every road.;\n	toroom $n giggles uncontrollably.;\n	}\n\nsay { \"service\";\n	toroom $n attempts a half-hearted attempt at looking offended.;\n	say Of course it\'s a service!;\n	say You think it is cheap...;\n	say to maintain a military presence on this road??;\n	grin %n;\n	}\n\ngive { \"-500\";\n	smile %n.;\n	say Thank you for your cooperation.;\n	say Have a nice day!;\n	say And remember: We are here for your protection!;\n	toggle 129;\n	}\n\nsay { \"protection\";\n	tonotvict $n growls at %n.;\n	tovict $n growls at you.;\n	say Well if you think we are not needed...;\n	say Try coming at night!;\n	grin %n;\n	}\n\nsay { \"Moath\";\n	toroom $n drops to one knee in reverence.;\n	say Moath is the master!;\n	say Hail to the arch-mage.;\n	}\n\n'),(1028,'pulse { \"\";\n  destination 240;\n  moveto;\n}'),(1029,'pulse { \"\";\n  destination 240;\n  moveto;\n}'),(10331,'# Mining Clerk in Lost Mines - 10331\n# Barter Quest for some porous bedrock - 223\n# Will trade for lodestone - 209\n# Written by Mithros\n# Copyright 1999 SneezyMUD Administration\n\nsay {\"hello\";\n smile %n;\n say How do you do.;\n toroom $n returns to his work.;\n toroom $n mumbles something about not enough lodestones.;\n}\n\nsay {\"lodestone\";\n say Yes I\'m short on lodestones right now.;\n say You\'d be amazed how easily you get lost in these mines.;\n say I have an abundance of porous bedrock thats been mined.;\n say We mine it for some of our mage customers.;\n say I\'m hoping to trade some of it for lodestones.;\n}\n\nsay {\"trade\";\n say If you have lodestones to trade...;\n say I have plenty of porous bedrock if you\'re interested.;\n say Just let me have the lodestones and I\'ll get the bedrock.;\n}\n\nsay {\"bedrock\";\n link say trade;\n}\n\nsay {\"buy\";\n say Afraid I can\'t sell the bedrock.;\n say Our contacts would be mad if they didn\'t get their cut.;\n say Best I can do is make a trade for the bedrock.;\n say I can calm them some if its a trade for something I needed.;\n say Right now I\'m in desperate need of more lodestones.;\n}\n\ngive {\"209\";\n toroom $n examines the lodestone.;\n say Yes this will work nicely.;\n toroom $n shuffles about looking for some bedrock.;\n load 223;\n give bedrock-component %n;\n say There you go, hope it serves you well.;\n toroom $n returns to his work.;\n}\n\n\n'),(104,'# Small Cat (Grimhaven)\n# Maror\n# Copyright 2004, SneezyMUD Development Team.  All rights reserved.\n\npat {\"me\";\n  purr;\n}\n'),(105,'# Small Dog, Grimhaven\n# Maror\n# Copyright 2004, SneezyMUD Development Team.  All rights reserved.\n\npat {\"me\";\n  tovict $n licks your hand.;\n  tonotvict $n licks %n\'s hand.;\n}\n'),(10509,'# Tired Shepherd (room 10509)\n# Mage Belt Quest\n# Created by Moath\n# Copyright 1998, SneezyMUD Development Team.  All rights reserved.\n\n\nroomenter {\"\";\nchecktoggle 189;\ntell %n Ho there traveler!;\ntell %n Could you possibly aid me in my plight?;\n}\n\nsay {\"plight\";\nchecktoggle 189;\ntonotvict $n looks at $N tiredly.;\ntovict $n looks at you tiredly.;\ntell %n Vicious wolves have been attacking the pens at night.;\ntell %n They always seem to get away before I find them.;\ntell %n If you could single-handedly vanquish one,;\ntell %n It might help discourage the others.;\ntell %n If you kill one of them around here,;\ntell %n Return here so I can show my gratitude.;\n}\n\nsay {\"aid\";\nlink say {\"plight\";\n}\n\nsay {\"wolves\";\nchecktoggle 189;\nnod %n;\ntell %n At night, a wolf will usually stalk the pens.;\n}\n\nsay {\"gratitude\";\nchecktoggle 189;\nsmile;\ntell %n Bring me back some proof that you killed the wolf,;\ntell %n And I will reward thee!;\ntell %n But do not bother with bringing back the corpse!;\ntell %n You will have to figure out what proof I seek.;\n}\n\nsay {\"reward\";\nchecktoggle 189;\ntell %n First bring me proof of the wolf\'s death!;\ntell %n Then I shall give you your reward.;\n}\n\ngive {\"7874\";\nchecktoggle 189;\ngasp;\ntell %n The symbol of the Shepherd\'s Crook,;\ntell %n Is recognized by all shepherds.;\ntell %n This was from one of the shepherds that,;\ntell %n Got killed by wolves a little while back.;\ntell %n It is good that the medallion returns to our hands.;\ntoroom $n unclasps a medallion from around his neck.;\nload 7875;\ngive medallion %n;\ntell %n Give this to the druid of Haon Dor.;\ntell %n With it she will know you have our gratitude.;\n} \n'),(10511,'# Stern Looking Shepherd\n# Pasture Gate Key Quest\n# Created by Cosmo\n# Copyright 1996, SneezyMUD Development Team.  All right reserved.\n\nsmile { \"me none\";\n	say What do you want, %n.;\n	}\n\nsay { \"hello\";\n	say Don\'t bother me %n, I\'m not in the mood.;\n	}\n\nsay { \"the key\";\n	say I am a little busy right now.;\n	toroom $n stifles a yawn with $s hand.;\n	say I don\'t usually do this.;\n	say Can I trust you if I just give you a key.;\n	say Are you reliable?;\n	}\n\nsay { open the gate\";\n	say I am a little busy right now.;\n	toroom $n stifles a yawn with $s hand.;\n	say Can I trust you if I just give you a key.;\n	say Are you reliable?;\n	}\n\nsay { \"am reliable\";\n	load 10513;\n	say Hmmm, Well.;\n	say I really don\'t like doing this;\n	whisper %n You screw up and cost me my job, you will be sorry!;\n	give key %n;\n	say Now, Go away!;\n	}\n\nnod { \"me\";\n	link say am reliable;\n	}\n\nsay { \"not reliable\";\n	say Hmmm, then piss off!;\n	fart %n;\n	}\n\n'),(10513,'# Lounging Shepherd\n# Pasture Gate Key Quest\n# Created by Cosmo\n# Copyright 1996, SneezyMUD Development Team.  All rights reserved.\n\nsay  { \"hello\";\n	say Hello, %n.;\n	say Settle in for a long wait unless you have a key.;\n	}\n\nsay  { \"help\";\n	say What can I do for you?;\n	say I don\'t know a lot, but I know a few things.;\n	}\n\nsmile { \"me\";\n	say Hello, %n.;\n	}\n\nsay { \"a key\";\n	say That seems to be my problem too.;\n	sigh;\n	say I seem to have lost my key.;\n	say You have to get the watchman to let you in...;\n	say but he seems to be on another one of his breaks.;\n	say I am gonna wait for him here.;\n	}\n\nsay { \"the key\";\n	link say a key;\n	}\n\n\nsay { \"open the gate\";\n	link say a key;\n	}\n\nsay { \"watchman\";\n	say I saw him going south as I came in.;\n	say He really pisses me off.;\n	say I have heard he spends all of his time...;\n	say back past that gate in the south when he should be working.;\n	say If you ask me, he should be fired.;\n	}\n\nsay { \"i am good\";\n	say Thats terrific, I think I need some of your drugs.;\n	}\n\ngive { \"10513\";\n	say Thank you %n;\n	stand;\n	unlock gate;\n	open gate;\n	say Hmmm, I do believe I will take a nap instead, but you go ahead.;\n	give key %n;\n	rest;\n	}\n\n'),(10701,'# King Urik\n# Bishops Quest - Gain the Scepter of Urik\n# Created by Mithros -\n# Copyright 1997 by SneezyMUD Development Team\n# Note - Second mob in the quest\n\n# Sorcerers Quest - Gain the Royal Signet of Urik\n# Created by Mithros -\n# Copyright 1997 by SneezyMUD Development Team\n# Note - Second mob in the quest\n\nsay {\"hi\";\n	grin;\n	say I see you have just arrived...;\n	say since you\'re among the living yet.;\n	say But thats OK, you\'ll join us soon enough.;\n	}\n\nsay { \"hello\";\n	link say hi;\n	}\n\ngive {\"7083\";\n	load 10784;\n	toroom $n scans over the note.;\n	say What does he think me a fool!;\n	say I\'ll not give that man nor any other my Scepter.;\n	think;\n	say I\'ll ask my Advisor what to do with this uprising.;\n	toroom $n scribbles a message across the top of the note.;\n     }\n\nsay {\"bishop Mercees\";\n	say Bishop Mercees is a weak old fool.;\n	say He thinks because I\'m dead...;\n	say he & Valteez can rule this Citadel.;\n 	say He\'ll learn soon enough I am fully capable of ruling yet.;\n	say First, I must remove the curse of the Obsidian Man from myself.;\n     }\n\nsay {\"advisor\";\n	say My Advisor?  Have you seen him lately?;\n	say He never seems to be around when I need him.;\n	say I\'m starting to question his loyalties to me.;\n	say Here I might have a message you can pass along to him.;\n 	give message %n;\n	}\n\nsay {\"message\";\n	say Message?  I\'d have one if anything interesting had happened.;\n	say Like last week the Bishop tried to pronounce me banished.;\n	say He learned without my Scepter no one will follow him.;\n	say Do you have any interesting news for me?;\n	say Or perhaps a note from some person?;\n	say If not then why do you bother me?;\n	}\n\ngive {\"7089\";\n	toroom $n scans over Valteez\'s message.;\n	load 10790;\n	laugh;\n	say What does this man think me, an ignorant fool?!?;\n	say My signet is perfectly fine the way it is.;\n	toroom $n looks up at you.;\n	say What your still here?  Be off, and send my servant to me.;\n	say She\'s been lax in her duties again...;\n	say and I have a list for her.;\n     }\n\nsay {\"servant\";\n	say Yes Mina, that lazy young one that cleans my quarters.;\n	say I have a list here for her to do when I see her next.;\n	say She especially needs to clean my Royal Coffer.;\n	say Even from here I can see the dust forming.;\n	}\n\nsay {\"list\";\n	say It\'s just a few things I\'ve noticed she\'s been missing lately.;\n	say I\'m much too busy to go hunting her down right now though.;\n	give list %n;\n	}\n\nsay {\"goodbye\";\n	say Yes goodbye goodbye, for now.;\n	say I\'m sure we\'ll meet here within my realm of the dead soon.;\n	cackle;\n 	}\n\n'),(10708,'# Royal Advisor\n# Bishop\'s Quest - Obtain the Royal Scepter\n# Created by Mithros \n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	frown;\n	say Now what do you want?;\n	say Can\'t you see I\'m much too busy to be bothered?;\n	say I must figure out a way to control the Obsidian man.;\n	}\n\ngive {\"10784\";\n	say I swear that man is a simpleton.;\n	say Does he think I have time to bother with these trivial matters?;\n	say I am too busy looking for some way to control the Obsidian Man.;\n	say Now leave me be.;\n	toroom $n drops the message in thin air and it vanishes.;\n 	load 10785;\n	toroom $n pulls something from where the message vanished.;\n	toroom $n places a tiny object into his pocket and turns away.;\n	}\n\nsay {\"obsidian man\";\n	say The Obsidian Man?;\n 	say Yes, his power is amazing, if only I can learn to harness it.;\n	say There is an obsidian earring which is said can control him.;\n	say But it keeps eluding me when I attempt to test its powers.;\n	}\n\nsay {\"obsidian earring\";\n	say Yes a small earring made of pure obsidian.;\n	say Every time I get it, its magic draws it away from me.;\n	say But I have some ideas how to stop it if I get it again.;\n	say Would you help me locate it?;\n	say I\'ll help you get what the Bishop seeks if you would.;\n	say Just say you\'ll agree to help.;\n	}\n\nsay {\"bishop\";\n	say I assume you know all about Bishop Mercees latest plot.;\n	say He seeks the King\'s Scepter in hopes of ruling the Citadel.;\n	say Right now he is stuck in a joint ruler-ship with Valteez.;\n	say While we are held in the Keep by the Obsidian Man\'s curse.;\n	say But the Scepter would let him gain power over Valteez.;\n	}\n\nsay {\"agree to help\";\n	say Great!  OK all I can tell you is to seek out Overseer Talrog.;\n	say He\'s in the Mines where the Obsidian Man was first unearthed.;\n	say Maybe I have a bauble here I\'ve been promising him.;\n	say It might make him more cooperative.;\n	give ring %n;\n	}\n\nsay {\"bauble\";\n	say Oh that, it\'s just a small ring for Talrog?;\n	say Generally he is more cooperative when I give him gifts.;\n	say But I\'m not sure I can really trust you to deliver it.;\n	say People have agreed before, then keep what I give them.;\n	say Maybe if you had proven your trust to me before.;\n	}\n\nsay {\"trust\";\n	say I trust people once they\'ve fulfilled a task given them.;\n	say Many times the King sends message to me via adventurers.;\n	say If I receive those I find I feel more at ease with the person.;\n	say If you can prove yourself and still want to help let me know.;\n	}\n\ngive {\"7087\";\n	load 10786;\n	say Ahh, you found it!  Here is my part of our bargain.;\n	toroom $n reaches into thin air pulling out a small wax key.;\n 	toroom $n drops the Obsidian Earring into thin air and it vanishes!;\n  	say You\'ll find the Scepter in the Treasure Chamber below.;\n	say The guard there should be able to open the appropriate chest.;\n 	give key %n;\n	cackle;\n	}\n'),(10709,'# Head Dungeon Guard\n# Sorcerer\'s Quest - Obtain the Royal Signet\n# Created by Mithros -\n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	toroom $n looks up at you with a disgusted look.;\n	say What do you want?;\n	}\n\nsay {\"Belith\";\n	shrug;\n	say So many people move through here, I can\'t remember them all.;\n	say Feel free to look in on my current guests if you want.;\n	}\n\ngive {\"10791\";\n	toroom $n reads the note.;\n	say Oh yes I remember this guy.  Sent him to the mines.;\n	say Just shortly before the King\'s curse took us all.;\n	load 10792;\n	}\n\nsay {\"mines\";\n	say Yes the mines below the Keep.;\n	say He should be among the slaves.;\n	say I suppose you inquire among them for his whereabouts.;\n	say But I\'ll warn you not to talk to any without a pass.;\n	say Talrog will become very upset if you do.;\n	say Most of the slaves are scared to talk if you don\'t have one.;\n	}\n\nsay {\"pass\";\n	shrug;\n	say Well I\'m not sure I should over step Talrog\'s boundaries.;\n	say Some days he gets rather upset if I give out passes for his mines.;\n	give pass %n;\n	}\n'),(10713,'# Obsidian Keep Librarian\n# Book Quest\n# Created by Mithros -\n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	say What do you want?;\n	say Well?;\n	say If you\'ve nothing to say get out of my way.;\n	say I\'ve got to look through a stack of books right now.;\n	}\n\nsay {\"books\";\n	say Books? Yeah we got them.  This is a library you know?;\n	growl;\n	say Now will you stop bothering me, I really am quite busy.;\n	}\n\nsay {\"tome of knowledge\";\n	say Yeah I got that one.;\n	say You need it?  I\'m sure we could come up with a trade.;\n	say It seems worthless to me.;\n	}\n\nsay {\"trade\";\n	say Well I\'ve especially been looking for a Tome of Alchemy.;\n	say I\'ll agree to give you my Tome of Knowledge if you get me that.;\n	}\n\ngive {\"30\";\n	grin;\n	load 10712;\n	say Ahh now this is something I want in my library.;\n	toroom $n hands the book off to a staff member as he passes by.;\n	toroom $n tells the staff member to catalog the Tome.;\n	say As agreed.;\n	give tome %n;\n	}\n'),(10727,'# Mina\n# Sorcerer\'s Quest - Obtain the Royal Signet of Urik\n# Created by Mithros \n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	say Hello, if you\'ll excuse me I have quite a bit of cleaning to do.;\n	toroom $n turns $s back on you fighting back a tiny sob.;\n	}\n\ngive {\"10790\";\n	load 10791;\n	toroom $n looks over the list.;\n	sigh;\n	say I guess I should get to these right away.;\n 	say I\'ve just not been myself lately.;\n	say Not since they took my Belith away.;\n	}\n\nsay {\"Belith\";\n	say Yes, Belith and I were to be married.;\n	say But he offended the King and was sent to the Dungeons.;\n	say I miss him so, I wish I could get a message to him.;\n	say But I am not allowed near the dungeons.;\n	say And each time I ask the guards down there...;\n	say they just laugh at me.;\n	}\n\nsay {\"message\";\n	say Oh, would you be so kind as to deliver a message to him?;\n	say Just let him I know I miss him and will wait for him.;\n	give description %n;\n	say Here is his description in case the guard has forgotten him.;\n	say He\'s about six feet tall and has shoulder length brown hair.;\n	say And the most beautiful blue eyes.;\n	}\n\nsay {\"Belith is dead\";\n	say I\'ll not believe it unless you bring me proof.;\n	toroom $n turns away from you.;\n	}\n\ngive {\"7090\";\n	load 10793;\n	toroom $n scans over the note as tears form in her eyes.;\n	toroom $n destroys the message as she attempts to compose herself.;\n	say My thanks for your efforts, please let me return the favor.;\n	say The spirit world whispers of what Sorcerer Valteez seeks.;\n	say Take this key and it will reward you with the King\'s Signet.;\n	say The King keeps it in his Royal Coffer in his quarters.;\n	toroom $n quickly forms a key from the essence of the spirit world.;\n	give key %n;\n	}\n\nsay {\"goodbye\";\n	wave;\n	say Goodbye, and good luck to you in all your adventures.;\n	}\n\n'),(10728,'# Royal Guard\n# Bishop\'s Quest - Gain the Royal Scepter of Urik\n# Created by Mithros -\n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	glare;\n	say Yes? What do you need?;\n	say If you haven\'t business here leave.;\n	}\n\nsay {\"scepter\";\n	say The King\'s Royal Scepter?  Yes it should be here.;\n	say But I can\'t help you unless you have a key.;\n	say Give me the key and I\'ll open the chest it should be in.;\n	}\n\ngive {\"10786\";\n	load 10787;\n	say One moment.  It should be here somewhere.;\n	toroom $n turns away looking for the appropriate chest.;\n	unlock chest-[obsquest1];\n 	}\n'),(10729,'# Royal Treasure Guard\n# Bishop\'s Quest - Gain the Royal Scepter of Urik\n# Created by Mithros \n# Copyright 1997 by SneezyMUD Development Team\n\nsay {\"hello\";\n	toroom $n quickly hides away a small pastry.;\n	say Yes? Did you need something?;\n	}\n\nsay {\"scepter\";\n	say Yes the King\'s Scepter is in the Treasure room beyond that door.;\n	toroom $n points towards the heavy door to the east.;\n	say You\'ll most likely need a key if you want to enter.;\n	}\n\nsay {\"treasure\";\n	say Yes if you\'re looking for the Keep\'s treasure room you\'ve found it.;\n	say It\'s just through that door to the east there.;\n	say I hope you have a key or a good sum of talens you\'d like to part with.;\n	}\n\nsay {\"money\";\n	say Well how about you give me what you have...;\n	say and we\'ll see what seems fair.;\n	say I hope you don\'t insult me like the last guy.;\n	say He thought I should betray my King for only 15,000 talens.;\n	}\n\nsay {\"key\";\n	say The key?  Yes I have the key to the treasure room.;\n	say I\'m afraid I\'m not just going to hand it over to you though.;\n	toroom $n licks a stray piece of icing from $s lips.;\n	}\n\ngive {\"7036\";\n	checkload 10797;\n	toroom $n whispers, Seeing how everyone else around here is dead.;\n	toroom $n whispers, I don\'t see the harm in letting you slip by this once.;\n	unlock door east;\n	toroom $n turns $s back to you taking large bite from $s pastry.;\n	}\n\ngive {\"-20000\";\n	checkload 10797;\n	toroom $n puts the talens in $s money pouch.;\n	toroom $n bounces the pouch in $s hand a couple times feeling the weight.;\n	toroom The pouch is suddenly gone in a misty haze to the spirit world.;\n	say Well that seemed about right to me.;\n	unlock door east;\n	say Lets just keep this to ourselves OK?;\n	}\n	\n'),(10919,'# The Hermit Jed\n# Ranger Quest Lvl 14 - Initiator\n# Created by Mithros\n# Copyright 1998, SneezyMUD Development Team.  All rights reserved.\n\n\nsay {\"hello\";\n        say What do you want?;\n        say Just leave me alone.;\n}\n\n#\n# Ranger Quest Lvl 14\n# \n\nsay {\"hunter\'s belt\";\n      checktoggle 174;\n      checkuntoggle 175;\n      checkuntoggle 176;\n      checkuntoggle 177;\n      checkuntoggle 178;\n      checkuntoggle 179;\n      checkuntoggle 180;\n      checkuntoggle 181;\n      checkuntoggle 182;\n      checkuntoggle 183;\n      checkuntoggle 184;\n      checkuntoggle 185;\n      checkuntoggle 186;\n      ponder %n;\n      say Yes it does seem your are ready for your belt.;\n      say I can provide you with more <W>details<1> if you ask.;\n}\n\nsay {\"hunters belt\";\n      link say hunter\'s belt;\n}\n\nsay {\"hunter\'s belt\";\n      checktoggle 186;\n      say It seems you have already completed that quest.;\n      say You will need to seek higher assistance...;\n      say If you feel you need to repeat it.;\n}\n\nsay {\"details\";\n      checktoggle 174;\n      checkuntoggle 175;\n      checkuntoggle 176;\n      checkuntoggle 177;\n      checkuntoggle 178;\n      checkuntoggle 179;\n      checkuntoggle 180;\n      checkuntoggle 181;\n      checkuntoggle 182;\n      checkuntoggle 183;\n      checkuntoggle 184;\n      checkuntoggle 185;\n      checkuntoggle 186;\n	say I must first must warn you.;\n        say As you should have been warned before.;\n	say As a Ranger\'s life is most often solitary.;\n	say This quest will follow that same principal.;\n	say You will be required to finish it honorably on your own.;\n	say Accepting no aid from others.;\n      say I will give you more <W>information<1> if you are ready.;\n}\n\nsay {\"information\";\n      checktoggle 174;\n      checkuntoggle 175;\n      checkuntoggle 176;\n      checkuntoggle 177;\n      checkuntoggle 178;\n      checkuntoggle 179;\n      checkuntoggle 180;\n      checkuntoggle 181;\n      checkuntoggle 182;\n      checkuntoggle 183;\n      checkuntoggle 184;\n      checkuntoggle 185;\n      checkuntoggle 186;\n      think;\n      say Living here in this Valley I have come to know..;\n      say all the creatures that reside here.;\n      say As